parameters.functionCode:
const kpis = $input.all()[0].json;
const projects = $input.all()[1].json;
const teams = $input.all()[2].json;

// Process KPIs with trends (mock previous week data)
const processedKPIs = {};
kpis.forEach(kpi => {
  const previousValue = kpi.value * (0.8 + Math.random() * 0.4); // Mock previous week
  const change = ((kpi.value - previousValue) / previousValue * 100).toFixed(1);

  processedKPIs[kpi.metric] = {
    current: kpi.value,
    previous: previousValue,
    change: change,
    trend: change > 0 ? 'up' : 'down',
    formatted: kpi.metric === 'revenue' ? `$${kpi.value.toLocaleString()}` : kpi.value.toLocaleString()
  };
});

// Process project health
const projectHealth = {
  onTrack: 0,
  atRisk: 0,
  blocked: 0,
  upcoming: []
};

projects.forEach(project => {
  if (project.status === 'active' && project.progress_percentage >= 70) {
    projectHealth.onTrack++;
  } else if (project.status === 'at_risk') {
    projectHealth.atRisk++;
  }

  if (project.blocked_tasks) {
    projectHealth.blocked++;
  }

  // Check if due within 2 weeks
  const dueDate = new Date(project.due_date);
  const twoWeeksFromNow = new Date();
  twoWeeksFromNow.setDate(twoWeeksFromNow.getDate() + 14);

  if (dueDate <= twoWeeksFromNow) {
    projectHealth.upcoming.push({
      name: project.project_name,
      dueDate: project.due_date,
      progress: project.progress_percentage
    });
  }
});

// Process team performance
const teamPerformance = teams.map(team => {
  const participationRate = (team.standup_participation / team.team_size * 100).toFixed(0);
  const productivity = (team.tasks_completed / team.team_size).toFixed(1);

  return {
    name: team.team_name,
    size: team.team_size,
    tasksCompleted: team.tasks_completed,
    avgCompletionTime: team.avg_completion_time ? team.avg_completion_time.toFixed(1) : 'N/A',
    participationRate: participationRate,
    productivity: productivity,
    rating: participationRate > 80 && productivity > 5 ? 'Excellent' : 
            participationRate > 60 && productivity > 3 ? 'Good' : 'Needs Improvement'
  };
});

return {
  json: {
    weekEnding: new Date().toISOString(),
    kpis: processedKPIs,
    projectHealth: projectHealth,
    projects: projects,
    teamPerformance: teamPerformance,
    generated: new Date().toISOString()
  }
};

