parameters.functionCode:
const report = $input.first().json;
const tasks = [];

// Create tasks for at-risk projects
if (report.projectHealth.atRisk > 0) {
  tasks.push({
    title: 'Review at-risk projects',
    description: `${report.projectHealth.atRisk} projects are marked as at-risk. Schedule review meetings with project managers.`,
    assignee: 'pmo@company.com',
    priority: 'high',
    due_date: new Date(Date.now() + 2 * 24 * 60 * 60 * 1000).toISOString()
  });
}

// Create tasks for blocked items
if (report.projectHealth.blocked > 0) {
  tasks.push({
    title: 'Resolve project blockers',
    description: `${report.projectHealth.blocked} projects have blocking issues. Coordinate with teams to resolve.`,
    assignee: 'operations@company.com',
    priority: 'urgent',
    due_date: new Date(Date.now() + 1 * 24 * 60 * 60 * 1000).toISOString()
  });
}

// Create tasks for underperforming teams
report.teamPerformance.forEach(team => {
  if (team.rating === 'Needs Improvement') {
    tasks.push({
      title: `Team performance review: ${team.name}`,
      description: `${team.name} team showing low performance metrics. Schedule 1:1 with team lead.`,
      assignee: 'hr@company.com',
      priority: 'medium',
      due_date: new Date(Date.now() + 3 * 24 * 60 * 60 * 1000).toISOString()
    });
  }
});

return tasks.map(task => ({ json: task }));
