{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "required": ["exam_id", "questions"],
  "properties": {
    "exam_id": {
      "type": "string",
      "description": "Unique identifier for the exam"
    },
    "questions": {
      "type": "array",
      "items": {
        "type": "object",
        "required": ["id", "question_text", "question_type", "difficulty", "points", "order_index", "multiple_choice_options"],
        "properties": {
          "id": {
            "type": "string",
            "description": "Unique identifier for the question"
          },
          "question_text": {
            "type": "string",
            "description": "The actual question text"
          },
          "question_type": {
            "type": "string",
            "enum": ["multiple_choice", "open_ended", "true_false"],
            "description": "Type of question"
          },
          "difficulty": {
            "type": "string",
            "enum": ["easy", "medium", "hard"],
            "description": "Difficulty level of the question"
          },
          "points": {
            "type": "number",
            "minimum": 1,
            "maximum": 10,
            "description": "Points awarded for correct answer"
          },
          "order_index": {
            "type": "number",
            "minimum": 0,
            "description": "Order of the question in the exam"
          },
          "multiple_choice_options": {
            "oneOf": [
              {
                "type": "object",
                "required": ["options", "correct_answer"],
                "properties": {
                  "options": {
                    "type": "array",
                    "items": {
                      "type": "string"
                    },
                    "minItems": 2,
                    "maxItems": 4,
                    "description": "Array of possible answers"
                  },
                  "correct_answer": {
                    "type": "string",
                    "description": "The correct answer from the options"
                  }
                }
              },
              {
                "type": "null",
                "description": "Null for non-multiple choice questions"
              }
            ],
            "description": "Multiple choice options object or null for non-multiple choice questions"
          }
        },
        "allOf": [
          {
            "if": {
              "properties": { "question_type": { "enum": ["multiple_choice"] } }
            },
            "then": {
              "properties": {
                "multiple_choice_options": {
                  "type": "object",
                  "required": ["options", "correct_answer"]
                }
              }
            }
          },
          {
            "if": {
              "properties": { "question_type": { "enum": ["open_ended", "true_false"] } }
            },
            "then": {
              "properties": {
                "multiple_choice_options": { "type": "null" }
              }
            }
          }
        ]
      }
    }
  }
}

